import { S3Client, PutObjectCommand, HeadObjectCommand } from "@aws-sdk/client-s3";
import fs from "fs";
import path from "path";
import mime from "mime-types";


// --- CONFIG ---
const accountId = "a74db7fd24fd9c88bd063e7b7ea3cda7";
const accessKeyId = "d351a684f82f6391aa3d30a0ae447605";
const secretAccessKey = "50facd2a33cbaecd14e1c0306155008fb4934f1515540e2e7576040bad296eab";
const bucketName = "criztec";
const publicDomain = "https://assets.criztec.com";
const postsDir = path.join(process.cwd(), "src/data/post");
const publicDir = path.join(process.cwd(), "public");

const S3 = new S3Client({
  region: "auto",
  endpoint: `https://${accountId}.r2.cloudflarestorage.com`,
  credentials: {
    accessKeyId,
    secretAccessKey,
  },
});

// Helper to check if file exists in S3 (avoid re-uploading if possible, though we might overwrite for updates)
async function checkExists(key) {
  try {
    await S3.send(new HeadObjectCommand({ Bucket: bucketName, Key: key }));
    return true;
  } catch (e) {
    return false;
  }
}

async function uploadToR2(buffer, key, contentType) {
  console.log(`Uploading ${key}...`);
  await S3.send(new PutObjectCommand({
    Bucket: bucketName,
    Key: key,
    Body: buffer,
    ContentType: contentType,
  }));
  return `${publicDomain}/${key}`;
}

async function processFile(filePath) {
  const content = fs.readFileSync(filePath, "utf-8");
  // Simple regex for frontmatter image. 
  // Assumes format: image: 'url' or image: url or image: "url"
  // We'll replace it carefully.
  const imageMatch = content.match(/^image:\s*(['"]?)(.*?)\1\s*$/m);
  
  if (!imageMatch) {
    console.log(`No image found in ${path.basename(filePath)}`);
    return;
  }

  const originalUrl = imageMatch[2];
  
  if (originalUrl.startsWith(publicDomain)) {
    console.log(`Already on R2: ${path.basename(filePath)}`);
    return;
  }

  let newUrl = "";
  
  try {
    if (originalUrl.startsWith("http")) {
      // External URL (e.g. pollination)
      console.log(`Processing external URL: ${originalUrl}`);
      const resp = await fetch(originalUrl);
      if (!resp.ok) throw new Error(`Failed to fetch ${originalUrl}`);
      const buffer = Buffer.from(await resp.arrayBuffer());
      const contentType = resp.headers.get("content-type") || "image/jpeg";
      const ext = mime.extension(contentType) || "jpg";
      
      // Generate filename from post slug + -image
      const slug = path.basename(filePath, ".md");
      const key = `${slug}-hero.${ext}`;
      
      newUrl = await uploadToR2(buffer, key, contentType);
      
    } else {
      // Local path
      // Could be relative or absolute-ish (e.g. /images/foo.png or ~/assets/foo.png)
      let localPath = originalUrl;
      
      // Handle '~/assets' alias if used in Astro
      if (localPath.startsWith("~/")) {
        // usually src/
        localPath = path.join(process.cwd(), "src", localPath.replace("~/", ""));
      } else if (localPath.startsWith("/")) {
        // usually public/
        localPath = path.join(publicDir, localPath);
      } else {
        // Verify if it exists relative to the MD file? Unlikely for Astro data collections usually, but strict check:
        // Or assume relative to src/assets?
        // Let's assume standard Astro public folder if relative, or resolve from file.
        // For src/data/post, images are often in public/ or src/assets.
        // Let's try to resolve it.
        const try1 = path.join(publicDir, localPath);
        if (fs.existsSync(try1)) {
            localPath = try1;
        } else {
             const try2 = path.join(path.dirname(filePath), localPath);
             if (fs.existsSync(try2)) localPath = try2;
        }
      }

      if (fs.existsSync(localPath)) {
        console.log(`Processing local file: ${localPath}`);
        const buffer = fs.readFileSync(localPath);
        const contentType = mime.lookup(localPath) || "image/jpeg";
        const key = path.basename(localPath); // Keep original filename for local files? Or standardize?
        // Let's standardize to avoid collisions if multiple posts use "hero.jpg"
        const slug = path.basename(filePath, ".md");
        const ext = path.extname(localPath);
        const newKey = `${slug}-hero${ext}`; // consistent naming
        
        newUrl = await uploadToR2(buffer, newKey, contentType);
      } else {
        console.error(`Could not find local file: ${originalUrl} for ${path.basename(filePath)}`);
        return;
      }
    }

    // Update the file
    if (newUrl) {
      const newContent = content.replace(originalUrl, newUrl);
      fs.writeFileSync(filePath, newContent, "utf-8");
      console.log(`Updated ${path.basename(filePath)}`);
    }

  } catch (err) {
    console.error(`Failed to process ${path.basename(filePath)}:`, err);
  }
}

async function main() {
  const files = fs.readdirSync(postsDir).filter(f => f.endsWith(".md"));
  console.log(`Found ${files.length} posts.`);
  
  for (const file of files) {
    await processFile(path.join(postsDir, file));
  }
}

main();
